import React, {useState} from 'react';
import Login from '../screens/login';
import Starter from '../screens/starter';
import BuySell from '../components/buySell';
import Content2 from '../screens/content2';
import Marketplace from '../screens/marketplace';
import Marketplace_item_Owner from "../screens/marketplace_item_Owner"
import AppDrawer from '../screens/appDrawer';
import Page from '../screens/page';
import SignUp from '../screens/signup';
import Account from '../screens/account';


const screens = () => {

    const [state, setState] = useState({
        err: "",
        page: 7,
        formdata: [],
        currentItem: "",
        category: [],
        current_user: "",
        itemOwner: "", // email of item owner
        currentCategory: "",
        currentDescription: "",
        currentPrice: ""
    })
    switch (state.page) {
        case 0:
          return <Login state={state}
          setState={setState}/>
        case 1:
          return <SignUp state={state}
          setState={setState}/>
        case 2:
          return <Content2 state={state}
          setState={setState}/>
            
        case 3:
          return <Marketplace state={state}
              setState={setState}/>
            
        case 4:<AppDrawer state={state}
        setState={setState}/>
        case 5:return <Account state={state}
        setState={setState}/>
        case 6:
            return <SplashScreen state={state}
                setState={setState}/>
        case 7:return <Page state={state}
        setState={setState}/>
            
        case 8:
          return <Marketplace_item_Owner state={state}
        setState={setState}/>

    }
};


export default screens;
