import React, {useState, useEffect} from 'react';
import {
    View,
    Text,
    Image,
    TouchableOpacity,
    StyleSheet,
    ScrollView
} from 'react-native';
import Header from './appDrawerHeader';
import firebase from '../firebase';


export default function Marketplace({state, setState}) {

    const [category, getCategory] = useState([])
    var current = "thab@gmail"

    useEffect(() => {
        var close;

        if (state.currentItem == "all") {
            close = firebase.firestore().collection("stock").onSnapshot(data => {
                setState({
                    ...state,
                    category: data.docs.map(item => item.data())
                })

            })
        } else {
            close = firebase.firestore().collection("stock").where("category", "==", state.currentItem).onSnapshot(data => {
                setState({
                    ...state,
                    category: data.docs.map(item => item.data())
                })

            })
        }


        return() => close()
    }, [])

    // function logME() {
    //     const dataRef = db.collection("stock").doc();
    //     return console.log(dataRef);
    // }


    return (<ScrollView>
        <Header state={state}
            setState={setState}/>
        <View styles={
            styles.content
        }> {
            state.category.map(item => (
                
                <TouchableOpacity  onPress={e=>setState({...state,page:9, itemOwner:item.email,})}>
                <View style={
                    styles.col1
                }>
                    <Text>Image</Text>
                    <Text> {
                        item.description
                    }</Text>
                    <Text> {
                        item.price
                    } </Text>


                </View>
            </TouchableOpacity>))
        }

            <View style={
                styles.col2
            }>
                <Text>
                    Col2
                </Text>

            </View>
        </View>

    </ScrollView>)
}

const styles = StyleSheet.create({
    content: {
        display: 'flex',
        flexDirection: 'row',
        flexWrap: 'wrap',
        padding: 5
    },
    col1: {
        flex: 1,
        padding: 5
    },
    col2: {
        flex: 1.5,
        padding: 5
    }
})
