import React, {useState, useEffect} from 'react';
import {
    View,
    Text,
    Image,
    TouchableOpacity,
    StyleSheet,
    ScrollView
} from 'react-native';
import Header from './backDrawerHeaderOwner';
import firebase from '../firebase';


export default function Marketplace({state, setState}) {

    const [quantity, getQuantity] = useState({item: 0})
    var current = "thab@gmail"

    useEffect(() => {
             
 
        var itemQ=0;
        var close = firebase.firestore().collection("stock")
        .where("category", "==", state.currentCategory)
        .onSnapshot(data =>{
             data.forEach(item=>{
                 itemQ+=parseInt(item.data()["quantity"])
             })

             firebase.firestore().collection("quantity").onSnapshot(item=>{
                   var current =item.docs.map(d=>d.data())
               
                   if(current.length>0){
                    var value=current[0][state.currentCategory]
                    getQuantity({item:itemQ-parseInt(value)})

                   }
             })
            

            }
        )

      
        return() => close()
    }, [])

  function buyItem(){
                       //    var keyObject=Object.keys(current[0]).filter(name=>name==state.currentCategory)
                    //    firebase.firestore().collection("quantity")
                    //    console.log(keyObject[0])
  }

    return (<ScrollView>
        <Header state={state}
            setState={setState}/>
        <View styles={
            styles.content
        }>

            <View style={
                styles.col2
            }>
                <Text> {quantity.item} </Text>

            </View>
            <Button onPress={buyItem} ></Button>
        </View>

    </ScrollView>)
}

const styles = StyleSheet.create({
    content: {
        display: 'flex',
        flexDirection: 'row',
        flexWrap: 'wrap',
        padding: 5
    },
    col1: {
        flex: 1,
        padding: 5
    },
    col2: {
        flex: 1.5,
        padding: 5
    }
})
