import React, {useState, useEffect} from 'react';
import Login from '../screens/login';
import Starter from '../screens/starter';
import BuySell from '../screens/buySell';
import Content2 from '../screens/content2';
import Marketplace from '../screens/marketplace';
import AppDrawer from '../screens/appDrawer';
import Page from '../screens/page';
import SignUp from '../screens/signupScreen';
import {
    View,
    Text,
    StyleSheet,
    Image,
    ScrollView
} from 'react-native';

// import Main from "../screens/MainScreen";
// import SplashScreen from "../screens/SplashScreen";
// import SignUpScreen from "../screens/SignUpScreen";
// import SigninScreen from "../screens/SigninScreen";
import {AuthContext} from "../components/context";
import AsyncStorage from "@react-native-community/async-storage";

const screens = () => {
    const initialLoginState = {
        isLoading: true,
        userName: null,
        userToken: null
    };

    const loginReducer = (prevState, action) => {
        switch (action.type) {
            case "RETRIEVE_TOKEN":
                return {
                    ...prevState,
                    userToken: action.userToken,
                    isLoading: false
                };
            case "LOGIN":
                return {
                    ...prevState,
                    userName: action.id,
                    userToken: action.token,
                    isLoading: false
                };
            case "LOGOUT":
                return {
                    ...prevState,
                    userName: null,
                    userToken: null,
                    isLoading: false
                };
            case "REGISTER":
                return {
                    ...prevState,
                    userName: action.id,
                    userToken: action.token,
                    isLoading: false
                };
        }
    };

    const [loginState, dispatch] = React.useReducer(loginReducer, initialLoginState);

    const authContext = React.useMemo(() => ({
        signIn: async (userName, password) => {
            // setUserToken("ll");
            // setIsLoading(false);
            let userToken;
            userToken = null;
            if (userName == "user" && password == "mf") {
                try {
                    userToken = "oiu";
                    await AsyncStorage.setItem("userToken", userToken);
                } catch (e) {
                    console.log(e);
                }
            }
            // console.log("user token: ", userToken);
            dispatch({type: "LOGIN", id: userName, token: userToken});
        },
        signOut: async () => {
            // setUserToken(null);
            // setIsLoading(false);
            try {
                await AsyncStorage.removeItem("userToken", userToken);
            } catch (e) {
                console.log(e);
            }
            dispatch({type: "LOGOUT"});
        },
        signUp: () => {
            // setUserToken("ll");
            // setIsLoading(false);
        }
    }), []);

    useEffect(() => {
        setTimeout(async () => { // setIsLoading(false);
            let userToken;
            userToken = null;
            try {
                userToken = await AsyncStorage.getItem("userToken", userToken);
            } catch (e) {
                console.log(e);
            }
            // console.log("user token: ", userToken);
            dispatch({type: "REGISTER", token: userToken});
        }, 1500);
    }, []);


    const RootStackScreen = ({navigation}) => (<RootStack.Navigator headerMode="none"> < RootStack.Screen name = "SplashScreen" component =
        { SplashScreen
    } /> <RootStack.Screen name="SigninScreen" component={SigninScreen} /> < RootStack.Screen name = "SignUpScreen" component =
        { SignUpScreen
    } /> </RootStack.Navigator>);

    return (<AuthContext.Provider value={authContext}> {
        loginState.userToken !== null ? <Main/>: <RootStackScreen/>
    } </AuthContext.Provider>);

    const [state, setState] = useState({err: "", page: 7, formdata: []})
    switch (state.page) {
        case 0:
            return <Starter state={state}
                setState={setState}/>
        case 1:
            return <AppDrawer state={state}
                setState={setState}/>
        case 2:
            return <BuySell state={state}
                setState={setState}/>
        case 3:
            return <Page state={state}
                setState={setState}/>
        case 4:
            return <Marketplace state={state}
                setState={setState}/>
        case 5:
            return <Content2 state={state}
                setState={setState}/>
        case 6:
            return <SplashScreen state={state}
                setState={setState}/>
        case 7:
            return <Login state={state}
                setState={setState}/>
        case 8:
            return <SignUp state={state}
                setState={setState}/>

    }
};


export default screens;
