import React, {useState, useEffect} from 'react';
import {
    View,
    Text,
    Image,
    TouchableOpacity,
    StyleSheet,
    ScrollView
} from 'react-native';
import Header from './appDrawerHeader';
import db from '../firebase';


export default function Marketplace({state, setState}) {

                const [category, getCategory] = useState([])
            var current = "thab@gmail"

                useEffect(() => {

                        var close = db.collection("stock").onSnapshot(data => {
                            setState({...state,category: data.docs.map(item=>item.data())})
                            console.log(data.docs.map(item=>item.data()))
                        }
                     )
                     console.log(state)
                    return() => close()},
                []
            )

            // function logME() {
            //     const dataRef = db.collection("stock").doc();
            //     return console.log(dataRef);
            // }
  

            return (<ScrollView>
                <Header state={state}
                    setState={setState}/>
                <view styles={
                    styles.content
                }>
                    <View style={
                        styles.col1
                    }>
                        <Text>
                            col 1
                        </Text>


                    </View>
                    <View style={
                        styles.col2
                    }>
                        <Text>
                            Col2
                        </Text>

                    </View>
                </view>

            </ScrollView>)
        }

        const styles = StyleSheet.create({
            content: {
                display: 'flex',
                flexDirection: 'row',
                flexWrap: 'wrap',
                padding: 5
            },
            col1: {
                flex: 1,
                padding: 5
            },
            col2: {
                flex: 1.5,
                padding: 5
            }
        })
