import React, {useState,useEffect} from "react";
import {
  StyleSheet,
  View,
  Text,
  TextInput,
  TouchableOpacity,
  Dimensions,
  StatusBar,
} from "react-native";
import { AuthContext } from "../components/context";
import * as Animatable from "react-native-animatable";
import FontAwesome from "react-native-vector-icons/FontAwesome";
import Feather from "react-native-vector-icons/Feather";
import firebase from '../firebase';


const SigninScreen = ({state, setState }) => {
 
  // const { signIn } = React.useContext(AuthContext);

  var [Form, updateForm] = useState({
    username: "",
    password: ""
})
  function changePage(){
    setState()
  }
  function getText(key, value) {
    updateForm({
        ...Form,
        [key]:value
    })

}
 console.log(Form)
  const loginHandle = (username, password) => {
    //signIn(username, password);

   try {
    firebase.auth().signInWithEmailAndPassword(username,password).then(e=>alert("welcome")).catch(item=>{
      console.log(item)
     if(item.code=="auth/user-not-found"){
       alert("Account doesn't exist!!")
     }else if(item.code=="auth/wrong-password"){
      alert("invalid password")
     }else{
        alert("sorry we have technical problem please try again!")
     }
    })
   } catch (error) {
    console.log(error)
   }
  };

  return (
    <View style={styles.container}>
      <StatusBar backgroundColor="#009387" barStyle="light-content" />
      <View style={styles.header}>
        <Text style={styles.text_header}>Sign In</Text>
      </View>

      <Animatable.View animation="fadeInUpBig" style={styles.footer}>
        <Text style={styles.text_footer}>Username</Text>
        <View style={styles.action}>
          <FontAwesome name="user-o" color="#05375a" size={20} />
          <TextInput
            placeholder="username"
            style={styles.textInput}
            autoCapitalize="none"
            onChangeText={
              e => getText("username", e)
          }
          />
        </View>

        <Text style={[styles.text_footer, { marginTop: 35 }]}>Password</Text>
        <View style={styles.action}>
          <FontAwesome name="lock" color="#05375a" size={20} />
          <TextInput
            placeholder="Password"
            style={styles.textInput}
            autoCapitalize="none"
            onChangeText={
              e => getText("password", e)
          }
          />

        </View>

        <View style={styles.button}>
          <TouchableOpacity
            onPress={() => {
              loginHandle(Form.username, Form.password);
            }}
            style={[
              styles.signIn,
              {
                backgroundColor: "#1E90FF",
                marginTop: 90,
              },
            ]}
          >
            <Text
              style={[
                styles.textSign,
                {
                  color: "#fff",
                },
              ]}
            >
              Sign In
            </Text>
          </TouchableOpacity>

          <TouchableOpacity
            //onPress={() => navigation.navigate("SignUpScreen")}
            style={[
              styles.signIn,
              {
                borderColor: "#1E90FF",
                borderWidth: 1,
                marginTop: 15,
              },
            ]}
          >
            <Text
              style={[
                styles.textSign,
                {
                  color: "#1E90FF",
                },
              ]}
            >
              Sign Up
            </Text>
          </TouchableOpacity>
        </View>
      </Animatable.View>
    </View>
  );
};

export default SigninScreen;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
  },
  header: {
    flex: 1,
    justifyContent: "flex-end",
    paddingHorizontal: 20,
    paddingBottom: 50,
  },
  footer: {
    flex: 3,
    backgroundColor: "#F4F6F6",
    borderTopLeftRadius: 30,
    borderTopRightRadius: 30,
    paddingHorizontal: 20,
    paddingVertical: 30,
  },
  text_header: {
    color: "#EC7063",
    fontWeight: "bold",
    fontSize: 30,
  },
  text_footer: {
    color: "#05375a",
    fontSize: 18,
  },
  action: {
    flexDirection: "row",
    marginTop: 10,
    borderBottomWidth: 1,
    borderBottomColor: "#f2f2f2",
    paddingBottom: 5,
  },
  textInput: {
    flex: 1,
    paddingLeft: 10,
    color: "#05375a",
  },
  signIn: {
    width: "100%",
    height: 50,
    justifyContent: "center",
    alignItems: "center",
    borderRadius: 10,
  },
  textSign: {
    fontSize: 18,
    fontWeight: "bold",
  },
});
