import React, {useEffect, useState} from 'react';
import Login from '../screens/login';
import Content2 from '../screens/content2';
import Marketplace from '../screens/marketplace';
import Marketplace_item_Owner from "../screens/marketplace_item_Owner"
import AppDrawer from '../screens/appDrawer';
import Page from '../screens/page';
import SignUp from '../screens/signup';
import Account from '../screens/account';
import {NetInfo, Platform} from "react-native";
import AsyncStorage from '@react-native-community/async-storage'
const screens = () => {

    const [state, setState] = useState({
        err: "",
        page: 0,
        formdata: [],
        currentItem: "",
        category: [],
        currentUser: "",
        itemOwner: "", // email of item owner
        currentCategory: "",
        currentDescription: "",
        currentPrice: ""
    })

    
    useEffect(() => {

        async function storeCurrentuser() {
            try {
                var email = await AsyncStorage.getItem("page").then(value => {
                    alert(value)
                    if(!isNaN(value)){
                        online();
                        setState({...state,page:2})
                    }else if(value==null){
                        setState({...state,page:0})
                    }else if (value == undefined) {
                        setState({
                            ...state,
                            page: 0
                        })
                    }
                })
    
            } catch (error) {
                
            }
        }
        try {
            storeCurrentuser();    
        } catch (error) {
            
        }
        
    }, [])


    function online() {
        switch (state.page) {
            case 0:
                return <Login state={state}
                    setState={setState}/>
            case 1:
                return <SignUp state={state}
                    setState={setState}/>
            case 2:
                return <Content2 state={state}
                    setState={setState}/>
            case 3:
                return <Marketplace state={state}
                    setState={setState}/>
            case 4:
                return <AppDrawer state={state}
                    setState={setState}/>
            case 5:
                return <Account state={state}
                    setState={setState}/>
            case 6:
                return <Marketplace_item_Owner state={state}
                    setState={setState}/>
            case 7:
                return <Page state={state}
                    setState={setState}/>


        }
    }
