import React, { useState, useEffect } from 'react';
import {
    View,
    Text,
    Image,
    TouchableOpacity,
    StyleSheet,
    ScrollView
} from 'react-native';
import Header from './appDrawerHeader';


export default function Marketplace({state, setState}) {

    //const [formdata, getFormData] = useState([])
    var current = "thab@gmail"
    useEffect(() => {

        var close = db.collection("stock").where("email", "==", current).onSnapshot(data =>{ setState({
            ...state,
            formdata: data.docs.map(userData => userData.data())
            
        }

        );console.log(Form.category);}
        )

        return() => close()

    }, [])


    return (

        <ScrollView>
            <Header state={state}
                setState={setState}/>
            <view styles={
                styles.content
            }>
                <View style={
                    styles.col1
                }>
                <Text>
                {close}
                </Text>


                </View>
                <View style={
                    styles.col2
                }>
                <Text>
                Col2
                </Text>

                </View>
            </view>

        </ScrollView>
    )
}

const styles = StyleSheet.create({
    content: {
        display: 'flex',
        flexDirection: 'row',
        flexWrap: 'wrap',
        padding: 5
    },
    col1: {
        flex: 1,
        padding: 5
    },
    col2: {
        flex: 1.5,
        padding: 5
    }
})
